import { useEffect, useState } from "react"
import { Card, Container, Row, Col, Image } from "react-bootstrap";
import axios from "axios";



const Trending = () => {
  const [movies, setMovies] = useState([])
  useEffect(() => {
    axios.get("https://api.themoviedb.org/3", {
      params: {
        api_key: ("da9c4514e37d4cd238d6e0205a120ed9")
      }
    }).then((response) => {
      setMovies(response.data.results)
    })
  }, [])


  return (
    <div>
      <Container>
        <br />
        <h1 className="text-white">TRENDING MOVIES</h1>
        <br />
        <Row>
          {movies.map((result, index) => {
            return (
              <Col md={4} className="movieWrapper" id="trending" key={index}>
                <Card className="movieImage">
                  <Image src={`${("https://image.tmdb.org/t/p/w500")}/${result.poster_path}`} alt="test" className="images" />
                  <div className="bg-dark">
                    <div className="p-2 m-1 text-white">
                      <Card.Title className="text-center">{result.title}</Card.Title>
                      <Card.Text className="text-left">
                        {result.overview}
                      </Card.Text>
                      <Card.Text className="text-left">
                        {result.release_date}
                      </Card.Text>
                    </div>
                  </div>
                </Card>
              </Col>
            )
          })}
        </Row>
      </Container>
    </div>
  )
}

export default Trending;
