import { Container, Row, Col, Image} from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import mazeRunner2 from '../assets/images/trending/maze runner.jpg';
import Thor4 from '../assets/images/trending/Thor4.jpg';
import Spiderman from '../assets/images/trending/The Amazing Spiderman2.jpg';
import BlackPanther from '../assets/images/trending/Wakanda Forever.jpg';
import Venom2 from '../assets/images/trending/Venom-2-poster.jpg'
import Avengers4 from '../assets/images/trending/Avengers4.webp'
import MoonKnight from '../assets/images/trending/MoonKnight.jpg'
import { useEffect } from 'react';
import axios from 'axios';
const Trending = () => {
    const [movies, setMovies] = useState([])
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_BASE_URL}/discover/movie`, {
            params: {
                api_key: process.env.REACT_APP_TMDB_KEY
            }
        }).then((response) => {
            setMovies(response.data.results)
        })
    }, [])
    return (
        <div>
            <Container>
                <br />
                <h1  className=' text-danger text-center'>TRENDING MOVIES</h1>
                <br />
                <Row>
                    {movies.map((result, index) => {
                        return(
                            <Col md= {3} className="movieWrapper movieImage" id="trending" key={index}>
                                <Card className="bg-dark text-light movieImage">
                                    <Image 
                                        src= { `${process.env.REACT_APP_IMG_URL}/${result.poster_path}` } 
                                        alt="Movie"
                                        class= {'border border-3 border-danger'}
                                    />
                                    <div className='bg-dark'>
                                        <div className='p-1 m-1'>
                                            <Card.Title className='text-center'>{result.title}</Card.Title>
                                            <Card.Text className='text-left'>
                                                {result.overview}
                                            </Card.Text>
                                            <Card.Text className='text-center'>{result.relesase_date}</Card.Text>
                                        </div>
                                    </div>
                                </Card>
                            </Col>
                        )
                    })}
                </Row>
            </Container>

        </div>
    )
}
export default Trending;
