import { useEffect, useState } from "react"
import { Container, Row } from "react-bootstrap";
import axios from "axios";
import MovieCard from "./MovieCard";



const Trending = () => {
  const [movies, setMovies] = useState([])
  useEffect(() => {
    axios.get("https://api.themoviedb.org/3/trending/all/day?api_key=da9c4514e37d4cd238d6e0205a120ed9").then(response =>(
      setMovies(response.data.results)
    )).catch(err=>{console.log(err)})
  }, [])


  return (
    <div>
      <Container>
        <br />
        <h1 className="text-white">TRENDING MOVIES</h1>
        <br />
        <Row>
          return <div className="flex pb-5 px-5 overflow-x-auto">
            {movies.map((movie, index) => {
              return <MovieCard key={index} {...movie} />
            })}
          </div>
        </Row>
      </Container>
    </div>
  )
}

<Row>
          {movies.map((result, index) => {
            return (
              <Col md={4} className="movieWrapper" id="trending" key={index}>
                <Card className="movieImage">
                  <Image src={`${process.env.REACT_APP_IMG_URL}/${result.poster_path}`} alt="test" className="images" />
                  <div className="bg-dark">
                    <div className="p-2 m-1 text-white">
                      <Card.Title className="text-center">{result.title}</Card.Title>
                      <Card.Text className="text-left">
                        {result.overview}
                      </Card.Text>
                      <Card.Text className="text-left">
                        {result.release_date}
                      </Card.Text>
                    </div>
                  </div>
                </Card>
              </Col>
            )
          })}
        </Row>

export default Trending;
